mapper.select:
SELECT T.ID TRIP_ID, DEPARTURE, DESTINATION, CREATED_TIME, DEPARTURE_TIME, TRIP_TYPE,TRIP_STATUS, P.ID PASSENGER_ID, D.ID DRIVER_ID FROM TRIP T LEFT OUTER JOIN PASSENGER P ON T.PASSENGER_ID=P.ID LEFT OUTER JOIN DRIVER D ON T.DRIVER_ID=D.ID WHERE T.TRIP_STATUS=#{tripStatus}
AND T.DEPARTURE_TIME > #{departureTime}
ORDER BY T.DEPARTURE_TIME
